function PlayerInfoBar({ data, roleLabel }) {
  const initial = data.name ? data.name.trim().split(" ").map(w => w[0]).join("").toUpperCase().slice(0, 2) : "?";
  return (
    <div style={{
      display: "flex", alignItems: "center", gap: 10, padding: "8px 16px",
      background: BRAND.white, borderBottom: `1px solid ${BRAND.grey200}`
    }}>
      <div style={{
        width: 32, height: 32, borderRadius: "50%", background: `linear-gradient(135deg, ${BRAND.blue}, ${BRAND.pink})`,
        display: "flex", alignItems: "center", justifyContent: "center", flexShrink: 0
      }}>
        <span style={{ color: BRAND.white, fontSize: 12, fontWeight: 800, fontFamily: "'Montserrat', sans-serif" }}>{initial}</span>
      </div>
      <div style={{ flex: 1, minWidth: 0 }}>
        <div style={{
          fontSize: 13, fontWeight: 700, color: BRAND.navyDark, fontFamily: "'Montserrat', sans-serif",
          whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis"
        }}>
          {data.name || "Player"}
        </div>
        <div style={{ fontSize: 10, color: BRAND.grey400, fontFamily: "'Montserrat', sans-serif" }}>
          {roleLabel}{data.squad ? ` • ${data.squad}` : ""}{data.club ? ` • ${data.club}` : ""}
        </div>
      </div>
    </div>
  );
}

function PageNav({ current, total, onPrev, onNext, labels }) {
  return (
    <div style={{
      display: "flex", alignItems: "center", justifyContent: "space-between", padding: "12px 16px",
      background: BRAND.white, borderTop: `1px solid ${BRAND.grey200}`, position: "sticky", bottom: 0, zIndex: 10
    }}>
      <button onClick={onPrev} disabled={current === 0}
        style={{
          padding: "8px 18px", borderRadius: 6, border: `1.5px solid ${current === 0 ? BRAND.grey200 : BRAND.navy}`,
          background: "transparent", color: current === 0 ? BRAND.grey400 : BRAND.navy, fontWeight: 700, fontSize: 12,
          fontFamily: "'Montserrat', sans-serif", cursor: current === 0 ? "default" : "pointer"
        }}>
        ← BACK
      </button>
      <div style={{ display: "flex", gap: 6, alignItems: "center" }}>
        {labels.map((l, i) => (
          <div key={i} style={{
            width: i === current ? 20 : 8, height: 8, borderRadius: 4, transition: "all 0.3s",
            background: i === current ? BRAND.pink : i < current ? BRAND.blue : BRAND.grey200
          }} />
        ))}
      </div>
      <button onClick={onNext} disabled={current === total - 1}
        style={{
          padding: "8px 18px", borderRadius: 6, border: "none",
          background: current === total - 1 ? BRAND.grey200 : `linear-gradient(135deg, ${BRAND.blue}, ${BRAND.pink})`,
          color: BRAND.white, fontWeight: 700, fontSize: 12,
          fontFamily: "'Montserrat', sans-serif", cursor: current === total - 1 ? "default" : "pointer"
        }}>
        NEXT →
      </button>
    </div>
  );
}

function PageLabel({ number, title }) {
  return (
    <div style={{ display: "flex", alignItems: "center", gap: 8, padding: "10px 16px", background: BRAND.grey50, borderBottom: `1px solid ${BRAND.grey200}` }}>
      <div style={{
        width: 24, height: 24, borderRadius: "50%", background: BRAND.pink, display: "flex", alignItems: "center", justifyContent: "center",
        fontSize: 11, fontWeight: 800, color: BRAND.white, fontFamily: "'Montserrat', sans-serif"
      }}>{number}</div>
      <div style={{ fontSize: 12, fontWeight: 700, color: BRAND.navy, textTransform: "uppercase", letterSpacing: 0.5, fontFamily: "'Montserrat', sans-serif" }}>{title}</div>
    </div>
  );
}

